class Turret:
    name = None
    flavor = None
    hp = None
    shield = None
    maxHeat = None
    coolingRate = None
    coolingDelay = None
    weapons = None
    unlock = None
    stats = None
    mechanics = None
    openSlots = 1
    
    def __init__(self, name, flavor, health, shield, maxHeat, coolingRate, coolingDelay, weapons, unlock, stats, mechanics, openSlots = 1):
        self.name = str(name)
        self.flavor = str(flavor)
        self.hp = str(health) if health > 0 else "#X"
        self.sp = str(shield) if shield > 0 else "#X"
        self.maxHeat = str(maxHeat)
        self.coolingRate = str(coolingRate)
        self.coolingDelay = str(coolingDelay)
        self.weapons = weapons
        self.unlock = unlock
        self.stats = stats
        self.mechanics = mechanics
        self.openSlots = openSlots
        
class Perk:
    name = None
    stats = None
    mechanics = None
    unlock = None
    
    def __init__(self, name, unlock, stats="", mechanics=""):
        self.name = str(name)
        self.stats = stats
        self.mechanics = mechanics
        self.unlock = str(unlock)
    
        
